import React from 'react';
import logo from './logo.png';
import './App.css';
import { Layout} from 'antd';

import Signup from './components/signup';
import Login from './components/login';
import Home from './components/home';
import ConfirmPay from './components/confirm-pay';

const { Header, Content, Footer } = Layout;



const Wrapper = (props) => (
  <Layout>
    <Header className="App-header">{props.header}</Header>
    <Content className="App-body">{props.body}</Content>
    <Footer className="App-footer">{props.footer}</Footer>
  </Layout>

);

const App = () => <Wrapper

  header = {
    <img src={logo} alt="" className="App-logo"></img>  
  }
  body = {
    <div>
    <Home/>
    <ConfirmPay/>
    <Login/>
    </div>
  }
  footer = {
    <h3 color="white">something here about signing up</h3>
  }
  />



export default App;
