import React from 'react';
import {Button} from 'antd';
import 'antd/dist/antd.css';

function ConfirmPay() {

    return(

        <span className = "confirm-span">
            <p style={{display: 'inline-block', color:'white'}}>Access this article for $.25?</p>
            <Button size = "small" type='danger'>Yes</Button>
            <Button size = "small" type='danger'>No</Button>
        </span>
    )

}

export default ConfirmPay
