import React from 'react';
import {Button} from 'antd';
import 'antd/dist/antd.css';

function ConfirmPay() {

    return(

        <span className = "confirm-span" style={{display: 'inline-block', margin:'10px'}}>
            <p style={{color:'white'}}>Access this article for $.25?</p>
            <Button size = "small" type='danger'>Yes</Button>
            <Button size = "small" type='danger'>No</Button>
        </span>
    )

}

export default ConfirmPay
