import React, {useState} from 'react';
import logo from './logo.png';
import './App.css';
// import { Layout} from 'antd';

// const { Header, Content, Footer } = Layout;
import Signup from './components/signup';
import Login from './components/login';
import Home from './components/home'; 
import ConfirmPay from './components/confirm-pay';

const Wrapper = (props) => (
  <div class="App">
    <header className="App-header">{props.header}</header>
    <body className="App-body">{props.body}</body>
    <footer className="App-footer">{props.footer}</footer>
  </div>

);

const App = () => {

  const [screen, setScreen] = useState(0)
  const progessScreen = () => setScreen(screen  + 1)
  const header = <img src={logo} className="App-logo"></img> 


  if(screen == 0){
    const body = <Login onClick={progessScreen}/>
    const footer = <h3 color="white">Something Here About Logging In</h3>
  }
  else if (screen == 1){
    const body = <Home onClick={progessScreen}/>
    const footer = <h3 color="white">Something Here about Balance</h3>
  }
  else if (screen == 2) {
    const body = <div><Home/><ConfirmPay/></div>
    const footer = <h3 color="white">Something Here about Confirming</h3>
  }

  <Wrapper
    header = {header}
    body = {body}
    footer = {footer}
    />

}


export default App;
